import cats from "./cats.js";
import { useState, useRef, useEffect } from "react";

function Card({ points, id, handleClick, data }) {
    let initStyle = "cardy-questions";
    let clickedStyle = "cardy-questions-clicked";

    const timesClicked = useRef(0);
    const isClicked = id === data.id;

    // Update the initial style based on whether the card has been clicked or is the double points card
    initStyle = (isClicked || (data.double_points && data.id === id)) ? clickedStyle : initStyle;

    const nestedHandleClick = (e) => {
        timesClicked.current++;
        handleClick(e); // Pass the click event back to the parent component
    };

    return (
        <div>
            <div id={id} className={initStyle} onClick={nestedHandleClick}>
                <h5 className="card-points">{points}</h5>
            </div>
        </div>
    );
}

function QuestionScreen({ question, answers, handleClick, double_points }) {
    const screenStyle = double_points ? "double-question-screen" : "question-screen";

    return (
        <div className={screenStyle}>
            <h5 className="question">{question}</h5>
            <ol className="answers">
                {answers.map((answer, index) => (
                    <li key={index} type="a">{answer}</li>
                ))}
            </ol>
            <button className="back-button" onClick={handleClick}>Back</button>
        </div>
    );
}

export default function Questions() {
    const [data, setData] = useState({ question: null, answers: null, id: null, double_points: false });
    const [loaded, setLoaded] = useState(false); // New state to control the loading of a random question
    const allData = cats.flat();

    useEffect(() => {
        const randomIndex = Math.floor(Math.random() * allData.length);
        console.log(allData[randomIndex]);
        const newData = { ...allData[randomIndex], double_points: true };
        // You may need to adjust this line if newData structure doesn't directly fit
        setData(newData);
    }, []);

    function handleQuesClick(event) {
        const id = parseInt(event.currentTarget.id, 10); // Ensure id is a number
        const selectedCard = allData.find(card => card.id === id);
        if (selectedCard) {
            setData({ ...selectedCard, double_points: selectedCard.double_points || false });
        }
    }

    function handleBackClick() {
        setData({ question: null, answers: null, id: null, double_points: false });
    }

    const catMap = cats.map((cat, index) => (
        <div className="ques" key={index}>
            {cat.map(card => (
                <Card
                    key={card.id}
                    points={card.points}
                    id={card.id}
                    handleClick={handleQuesClick}
                    data={data}
                />
            ))}
        </div>
    ));

    return (
        <div className="questions">
            <div className="ques-thingy">{catMap}</div>
            {data.question && <QuestionScreen {...data} handleClick={handleBackClick} />}
        </div>
    );
}
