import cats from "./cats.js";
import { useState, useRef, useEffect } from "react";

function Card(props) {
    let initStyle = "cardy-questions";
    let clickedStyle = "cardy-questions-clicked";

    const timesClicked = useRef(0);

    if (timesClicked.current !== 0 || props.id - props.data.id === 0) {
        initStyle = clickedStyle;
    }

    const nestedHandleClick = (e) => {
        timesClicked.current++;
        props.handleClick(e);
    };

    return (
        <div>
            <div id={props.id} className={initStyle} onClick={nestedHandleClick}>
                <h5 className="card-points">{props.points}</h5>
            </div>
        </div>
    );
}

function QuestionScreen(props) {
    const screenStyle = props.double_points ? "double-question-screen" : "question-screen";

    return (
        <div className={screenStyle}>
            <h5 className="question">{props.question}</h5>
            <ol className="answers">
                {props.answers.map((answer, index) => (
                    <li key={index} type="a">
                        {answer}
                    </li>
                ))}
            </ol>
            <button className="back-button" onClick={props.handleClick}>
                Back
            </button>
        </div>
    );
}

export default function Questions() {
    const [data, setData] = useState({ question: null, answers: null, id: null, double_points: false });
    const [randomIndex, setRandomIndex] = useState(null); // Store the random index independently
    const allData = [];

    cats.forEach((cat) => cat.forEach((card) => allData.push(card)));

    useEffect(() => {
        setRandomIndex(Math.floor(Math.random() * allData.length)); // Generate random index once
    }, []);

    useEffect(() => {
        if (randomIndex !== null) {
            const newData = allData.map((card, index) => ({
                ...card,
                double_points: index === randomIndex, // Assign double_points to the random card
            }));
            // Pass the updated double_points state correctly
            setData({ question: null, answers: null, id: null, double_points: newData[randomIndex].double_points });
        }
    }, [randomIndex, allData]);

    function handleQuesClick(event) {
        const id = event.currentTarget.id;
        const question = allData[id - 1].question;
        const answers = allData[id - 1].answers;
        setData({ question: question, answers: answers, id: id, double_points: data.double_points });
    }

    function handleBackClick() {
        setData({ question: null, answers: null, id: null, double_points: false });
    }

    const catMap = cats.map((cat) => {
        const cardMap = cat.map((card) => (
            <Card
                points={card.points * (card.double_points ? 2 : 1)}
                id={card.id}
                double_points={card.double_points}
                handleClick={handleQuesClick}
                data={data}
            />
        ));
        return <div className="ques">{cardMap}</div>;
    });

    return (
        <div className="questions">
            {/* ... rest of the component remains unchanged ... */}
        </div>
    );
}
